  #pragma warning(disable:4035 4793)

  __inline ULONGLONG
  __stdcall
  Int64ShllMod32 (
  ULONGLONG Value,
  DWORD ShiftCount
  )
  {
  __asm    {
  mov     ecx, ShiftCount
  mov     eax, dword ptr [Value]
  mov     edx, dword ptr [Value+4]
  shld    edx, eax, cl
  shl     eax, cl
  }
  }

  __inline LONGLONG
  __stdcall
  Int64ShraMod32 (
  LONGLONG Value,
  DWORD ShiftCount
  )
  {
  __asm {
  mov     ecx, ShiftCount
  mov     eax, dword ptr [Value]
  mov     edx, dword ptr [Value+4]
  shrd    eax, edx, cl
  sar     edx, cl
  }
  }

  __inline ULONGLONG
  __stdcall
  Int64ShrlMod32 (
  ULONGLONG Value,
  DWORD ShiftCount
  )
  {
  __asm    {
  mov     ecx, ShiftCount
  mov     eax, dword ptr [Value]
  mov     edx, dword ptr [Value+4]
  shrd    eax, edx, cl
  shr     edx, cl
  }
  }
