  __forceinline
  PVOID
  RtlSecureZeroMemory(
  PVOID ptr,
  SIZE_T cnt
  )
  {
  volatile char *vptr = (volatile char *)ptr;

  while (cnt) {
  *vptr = 0;
  vptr++;
  cnt--;
  }
  return ptr;
  }

