  __forceinline
  LONGLONG
  InterlockedXor64 (
  LONGLONG volatile *Destination,
  LONGLONG Value
  )
  {
  LONGLONG Old;

  do {
  Old = *Destination;
  } while (InterlockedCompareExchange64(Destination,
  Old ^ Value,
  Old) != Old);

  return Old;
  }

  __forceinline
  LONGLONG
  InterlockedIncrement64 (
  LONGLONG volatile *Addend
  )
  {
  LONGLONG Old;

  do {
  Old = *Addend;
  } while (InterlockedCompareExchange64(Addend,
  Old + 1,
  Old) != Old);

  return Old + 1;
  }

  __forceinline
  LONGLONG
  InterlockedDecrement64 (
  LONGLONG volatile *Addend
  )
  {
  LONGLONG Old;

  do {
  Old = *Addend;
  } while (InterlockedCompareExchange64(Addend,
  Old - 1,
  Old) != Old);

  return Old - 1;
  }

  __forceinline
  LONGLONG
  InterlockedExchange64 (
  LONGLONG volatile *Target,
  LONGLONG Value
  )
  {
  LONGLONG Old;

  do {
  Old = *Target;
  } while (InterlockedCompareExchange64(Target,
  Value,
  Old) != Old);

  return Old;
  }

  __forceinline
  LONGLONG
  InterlockedExchangeAdd64(
  LONGLONG volatile *Addend,
  LONGLONG Value
  )
  {
  LONGLONG Old;

  do {
  Old = *Addend;
  } while (InterlockedCompareExchange64(Addend,
  Old + Value,
  Old) != Old);

  return Old;
  }
  
